class WebSocketClient {
  constructor(uri) {
    this.URI = uri;
    this._resetDelay();
    this._socket = null;
    this._isOpen = false;
    this._shouldClose = false;
    this._delayUntilReconnect = null;
    this.openHandler = null;
    this.messageHandler = null;
    this.closeHandler = null;
  }

  open() {
    if (!window.WebSocket) {
      return;
    }

    this._shouldClose = false;

    this._socket = new WebSocket(this.URI);
    this._socket.onopen = this._onopen.bind(this);
    this._socket.onmessage = this._onmessage.bind(this);
    this._socket.onclose = this._onclose.bind(this);
  }

  send(message) {
    if (this._isOpen) {
      this._socket.send(message);
    }
  }

  close() {
    if (!this._isOpen) {
      return;
    }
    this._shouldClose = true;
    this._socket.close();
  }

  reconnect() {
    if (!this._isOpen) {
      return;
    }
    this._socket.close();
  }

  getReconnectDelay() {
    return this._delayUntilReconnect;
  }

  _onopen() {
    this._isOpen = true;
    this._delayUntilReconnect = 0;

    if (this.openHandler) {
      this.openHandler();
    }
  }

  _resetDelay() {
    this._delayUntilReconnect = 2000;
  }

  _onmessage(e) {
    var data = e.data;
    if (this.messageHandler) {
      this.messageHandler(data);
    }
  }

  _onclose() {
    this._isOpen = false;
    var done = false;

    if (this.closeHandler) {
      done = this.closeHandler();
    }

    if (!done && !this._shouldClose) {
      setTimeout(this._reconnect.bind(this), this._delayUntilReconnect);
    }
  }

  _reconnect() {
    if (!this._delayUntilReconnect) {
      this._resetDelay();
    } else {
      this._delayUntilReconnect = this._delayUntilReconnect * 2;
    }

    this._delayUntilReconnect = Math.min(this._delayUntilReconnect, 300000);
    this.open();
  }
}

