socket.onmessage = function(event) {
  const data = JSON.parse(event.data);
  if (data.type === 'result') {
    updateTestStatus(data.testId, data.status, data.result);
  } else if (data.type === 'status') {
    updateTestStatus(data.testId, data.status);
  }
};

function updateTestStatus(testId, status, result = null) {
  const statusElement = document.getElementById(`status-${testId}`);
  if (statusElement) {
    statusElement.textContent = status;
    if (result !== null) {
      const resultElement = document.getElementById(`result-${testId}`);
      resultElement.textContent = result;
    }
  }
}

function processQueue() {
