class Client {
    constructor(req, socket, head, options) {
        this.ws = new WebSocket(req.url);
        this.options = options;
        this.id = this.uniqueId(options.prefix);
        this.plugins = [];
        this.url = req.url;
        this.ws.onmessage = this.message.bind(this);
        this.ws.onclose = this.close.bind(this);
    }

    message(event) {
        const data = this._data(event.data);
        if (data.command) {
            switch (data.command) {
                case 'info':
                    this.info(data.data);
                    break;
                case 'reload':
                    this.reload(data.data.files);
                    break;
                default:
                    console.log('Unknown command:', data.command);
            }
        }
    }

    close(event) {
        this.emit('end', event);
    }

    hello() {
        const data = {
            command: 'hello',
            protocols: this.options.protocols,
            serverName: this.options.serverName
        };
        this.send(data);
    }

    info(data) {
        this.plugins = data.plugins;
        this.url = data.url;
    }

    reload(files) {
        files.forEach(file => {
            const data = {
                command: 'reload',
                file: file,
                liveReload: this.options.liveReload
            };
            this.send(data);
        });
    }

    _data(event) {
        return JSON.parse(event);
    }

    send(data) {
