  constructor() {
    super();
    this.DISCONNECT_TIMEOUT = 5e3;
    this.RECONNECT_TIMEOUT = 15e3;
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    this.wsURL = "";
    this.connectTS = 0;
    this.disconnectTID = 0;
    this.reconnectTID = 0;
    this.onmessage = null;
  }

  onconnect() {
    if (!this.isConnected || !this.wsURL || this.ws || this.pc) return false;
    this.wsState = WebSocket.CONNECTING;
    this.connectTS = Date.now();
    this.ws = new WebSocket(this.wsURL);
    this.ws.binaryType = "arraybuffer";
    this.ws.addEventListener("open", (e) => this.onopen(e));
    this.ws.addEventListener("close", (e) => this.onclose(e));
    return true;
  }

  ondisconnect() {
    this.wsState = WebSocket.CLOSED;
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    this.disconnectTID = 0;
  }

  onopen() {
    this.wsState = WebSocket.OPEN;
    this.ws.addEventListener("message", (e) => {
      if ("string" == typeof e.data) {
        const t = JSON.parse(e.data);
        for (const e in this.onmessage) this.onmessage[e](t);
      }
    });
    this.onmessage = {};
  }

  onclose() {
    if (this.wsState === WebSocket.CLOSED) return false;
    this.wsState = WebSocket.CONNECTING;
    this.ws = null;
    const e = Math.max(this.RECONNECT_TIMEOUT - (Date.now() - this.connectTS), 0);
    this.reconnectTID = setTimeout(() => {
      this.reconnectTID = 0;
      this.onconnect();
    }, e);
    return true;
  }

  send(e) {
    this.ws && this.ws.send(JSON.stringify(e));
  }

  reconnect() {
    if (this.wsState !== WebSocket.CLOSED) {
      this.ws?.addEventListener("close", () => this.onconnect());
      this.ondisconnect();
    } else {
      this.ondisconnect();
      this.onconnect();
    }
  }
