import websocket
import json
import time

class WebSocketClient:
    DISCONNECT_TIMEOUT = 5000
    RECONNECT_TIMEOUT = 10000

    def __init__(self, wsURL, onmessage=None):
        self.wsState = websocket.CLOSED
        self.ws = None
        self.wsURL = wsURL
        self.onmessage = onmessage
        self.connectTS = 0
        self.disconnectTID = None
        self.reconnectTID = None

    def onconnect(self):
        if self.wsState != websocket.OPEN:
            self.ws = websocket.WebSocketApp(self.wsURL, on_open=self.onopen, on_close=self.onclose, on_error=self.onerror)
            self.ws.on_message = self.onmessage
            self.ws.run_forever()
            return True
        return False

    def ondisconnect(self):
        if self.wsState != websocket.CLOSED:
            self.wsState = websocket.CLOSED
            if self.disconnectTID:
                self.disconnectTID.cancel()
                self.disconnectTID = None
            if self.reconnectTID:
                self.reconnectTID.cancel()
                self.reconnectTID = None
            self.ws.close()

    def onopen(self, e):
        self.wsState = websocket.OPEN
        self.connectTS = time.time()
        self.disconnectTID = None
        self.reconnectTID = None

    def onclose(self):
        self.wsState = websocket.CLOSED
        self.schedule_reconnect()

    def onerror(self, e):
        print("WebSocket error:", e)

    def send(self, e):
        if self.wsState == websocket.OPEN:
            self.ws.send(json.dumps(e))

    def reconnect(self):
        self.ondisconnect()
        self.onconnect()

    def schedule_reconnect(self):
        if not self.reconnectTID:
            self.reconnectTID = threading.Timer(self.RECONNECT_TIMEOUT, self.onconnect)
            self.reconnectTID.start()
