const axios = require("axios");

class QuqiClient {
    constructor(config) {
        this.headers = {
            cookie: config.quqi.cookie,
            referer: "https://quqi.com/"
        };
    }

    async _sleep(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    async getDailyTask() {
        try {
            let res = await axios.get(`https://exp.quqi.com/v1/dailyTask/state?_=1${Date.now()}`, {headers: this.headers});
            if(res.data.err == 0) {
                let list = res.data.data.filter(x => x.status != 2);
                console.log(`剩余${list.length}任务未完成`);
                return list;
            }
        } catch (err) {
            console.log(err);
        }
        return null;
    }

    async getReward(id) {
        try {
            let res = await axios.get(`https://exp.quqi.com/v1/dailyTask/sendAward/${id}`, {headers: this.headers});
            console.log("领取奖励：" + res.data.msg);
        } catch (err) {
            console.log(err);
        }
    }

    async visit(id) {
        try {
            this.headers["User-Agent"] = "Mozilla/5.0 (Linux; Android 11; Redmi K30) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.120 Mobile Safari/537.36";
            let res = await axios.post(`https://quqi.com/api/dir/ls?quqi_id=${id}`, `quqi_id=${id}&tree_id=1&node_id=0`, {headers: this.headers});
            let res1 = await axios.post(`https://quqi.com/auth/updateVisit?quqi_id=${id}`, `quqi_id=${id}`, {headers: this.headers});
            console.log("浏览多人群组：" + res1.data.msg);
            await this._sleep(1000);
            await this.getReward(14);
        } catch (err) {
            console.log(err);
        }
    }

    async getQuqiId() {
        try {
            let res = await axios.get("https://group.quqi.com/v1/group/list", {headers: this.headers});
            if (res.data.msg == "success") {
                let gplist = res.data.data.filter(x => x.type == 14);
                let quqid = gplist.length == 0 ? res.data.data[0].quqi_id : gplist[0].quqi_id;
                let gtype = gplist.length == 0 ? 1 : 2;
                if (gtype == 1) {
                    console.log("🈚️多人群组,请自行创建一个多人群组,或者申请加入https://quqi.avyeld.com/join/company/team.html?jointarget=ed521eef-7ed1-486f-a903-2457ebe5704f");
                    let sqres = await axios.post("https://group.quqi.com/v1/application", "credential=ed521eef-7ed1-486f-a903-2457ebe5704f", {headers: this.headers});
                    console.log("申请加入多人群组：" + sqres.data.msg);
                }
                return {
                    qid: quqid,
                    gtype: gtype
                };
            }
        } catch (err) {
            console.log(err);
        }
        return null;
    }

    async uploadImg(id) {
        try {
            let name = `每日签到${Date.now()}`;
            this.headers["User-Agent"] = "Mozilla/5.0 (Linux; Android 11; Redmi K30) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.120 Mobile Safari/537.36";
            let res = await axios.post(`https://quqi.com/api/upload/v1/file/init?quqi_id=${id}`, `file_name=${name}.jpg&is_slice=false&md5=507cf982c5db90bc35f9c6c8179c292f&parent_id=0&quqi_id=${id}&sha=4b4afc24113d86d0afd9be21b6841d9c627cdabf01317e3e760308b05f8bdd81&sign=617db783e0ba01343338ee9887dfdb18&size=331665&tree_id=1`, {headers: this.headers});
            if (res.data.msg == "ok") {
                console.log(`上传图片 ${name} 成功`);
                let nid = res.data.data.node_id;
                if (nid) await axios.post(`https://quqi.com/api/node/batchDel?quqi_id=${id}`, `quqi_id=${id}&tree_id=1&node_ids=${nid}`, {headers: this.headers});
                await this._sleep(1000);
                await this.getReward(15);
            } else {
                console.log("上传失败：" + res.data.msg);
            }
        } catch (err) {
            console.log(err);
        }
    }

    async executeTasks() {
        let quqinfo = "";
        let dailyTaskList = await this.getDailyTask();
        let qgp = await this.getQuqiId();

        if (dailyTaskList) {
            for (let task of dailyTaskList) {
                console.log(task.task_decreption);
                if (task.status == 1) {
                    await this.getReward(task.task_type);
                } else if (task.task_type == 14 && task.status == 0 && qgp.gtype == 2) {
                    await this.visit(qgp.qid);
                } else if (task.task_type == 15 && task.status == 0) {
                    await this.uploadImg(qgp.qid);
                }
            }
        }

        let ires = await axios.get("https://api.quqi.com/vipWallet/v1/vipAndWallet", {headers: this.headers});
        if (ires.data.msg == "success") {
            quqinfo = `曲奇饼：${ires.data.data.wallet_info.cookie}  曲奇豆：${ires.data.data.wallet_info.bean}`;
        } else {
            quqinfo = ires.data.msg;
        }
        console.log(quqinfo);
        return "【曲奇网盘】：" + quqinfo;
    }
}

