export const initWs = (
  handleFunctionCall: Function,
  url: string,
  cb: (_ws: WebSocket) => void
) => {
  const ws = new WebSocket(url);
  ws.onopen = () => {
    console.log("Connected to server");
    postMessage("msg:Connected to server");
    postMessage({
      id: getUuid(),
      data: null,
      type: MsgType.WebSocketConnected,
    });
  };
  ws.onmessage = (e) => {
    const channel = new MessageChannel();
    const msg = deserializedMsg(e.data);
    handleFunctionCall(msg.data, msg.id, channel.port1);
    channel.port2.onmessage = (e) => {
      ws.send(JSON.stringify(e.data));
    };
  };
  ws.onclose = () => {
    console.log("Disconnected from server");
    postMessage({
      id: getUuid(),
      data: null,
      type: MsgType.WebSocketDisconnected,
    });
    setTimeout(() => {
      initWs(handleFunctionCall, url, cb);
    }, 1000);
  };
  cb(ws);
};

