  constructor(url, options) {
    this.url = url;
    this.options = options;
    this.socket = null;
    this.streaming = false;
    this.callbacks = {
      onOpen: [],
      onClose: [],
      onError: [],
      onMessage: []
    };
    this.destination = null;
    this.reconnectInterval = options.reconnectInterval || 5000;
    this.shouldAttemptReconnect = options.shouldAttemptReconnect || true;
    this.completed = false;
    this.established = false;
    this.progress = 0;
    this.reconnectTimeoutId = null;
    this.onEstablishedCallback = options.onEstablishedCallback;
    this.onCompletedCallback = options.onCompletedCallback;
    this.hookOnEstablished = options.hookOnEstablished;
  }

  connect(destination) {
    this.destination = destination;
  }

  destroy() {
    if (this.socket) {
      this.socket.close();
      this.socket = null;
      this.completed = true;
      this.established = false;
      clearTimeout(this.reconnectTimeoutId);
    }
  }

  start() {
    this.socket = new WebSocket(this.url);
    this.socket.onopen = this.onOpen.bind(this);
    this.socket.onclose = this.onClose.bind(this);
    this.socket.onerror = this.onError.bind(this);
    this.socket.onmessage = this.onMessage.bind(this);
  }

  resume() {}

  onOpen() {
    this.established = true;
    this.progress = 100;
    this.callbacks.onOpen.forEach(callback => callback());
    if (this.onEstablishedCallback) {
      this.onEstablishedCallback();
    }
    if (this.hookOnEstablished) {
      this.hookOnEstablished();
    }
  }

  onClose(event) {
    this.established = false;
    this.callbacks.onClose.forEach(callback => callback(event));
    if (this.shouldAttemptReconnect) {
      this.reconnect();
    }
  }

  onMessage(event) {
    if (this.destination) {
      this.destination.write(event.data);
    }
    this.callbacks.onMessage.forEach(callback => callback(event));
  }

  reconnect() {
    clearTimeout(this.reconnectTimeoutId);
    this.reconnectTimeoutId = setTimeout(() => {
      if (!this.completed) {
        this.start();
      }
    }, this.reconnectInterval);
  }
}

