```javascript
function echo() {
  const wsUri = 'ws://' + window.location.host + window.location.pathname;
  const ws = new WebSocket(wsUri);

  ws.onopen = function() {
    console.log('WebSocket connection established');
    document.getElementById('status').textContent = 'Connected';
    const myField = document.getElementById('myField');
    ws.send(myField.value);
    document.getElementById('message').textContent = 'Message sent: ' + myField.value;
  };

  ws.onmessage = function(event) {
    console.log('Message received: ' + event.data);
    document.getElementById('message').textContent = 'Message received: ' + event.data;
  };

  ws.onclose = function() {
    console.log('WebSocket connection closed');
    document.getElementById('status').textContent = 'Disconnected';
  };

  ws.onerror = function(error) {
    console.log('WebSocket error: ' + error);
    document.getElementById('status').textContent = 'Error';
  };
}
