function echo() {
  const wsUri = 'ws://' + window.location.host + window.location.pathname;
  const ws = new WebSocket(wsUri);

  ws.onopen = function(event) {
    console.log('Connection established');
    document.getElementById('status').innerText = 'Connected';
    const myField = document.getElementById('myField');
    ws.send(myField.value);
    document.getElementById('message').innerText = 'Message sent: ' + myField.value;
  };

  ws.onmessage = function(event) {
    console.log('Message received: ' + event.data);
    document.getElementById('message').innerText += '\nReceived: ' + event.data;
  };

  ws.onclose = function(event) {
    console.log('Connection closed');
