class ReconnectingWebSocket {
  constructor(url, protocols, options) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen || true;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this.protocol = null;
    this.ws = null;
    this.listeners = {};
    this.setup();
  }

  addEventListener(type, listener) {
    if (!this.listeners[type]) {
      this.listeners[type] = [];
    }
    this.listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this.listeners[type]) {
      const index = this.listeners[type].indexOf(listener);
      if (index !== -1) {
        this.listeners[type].splice(index, 1);
      }
    }
  }

  dispatchEvent(event) {
    if (this.listeners[event.type]) {
      this.listeners[event.type].forEach(listener => listener(event));
    }
  }

  open(reconnecting) {
    if (this.ws) {
      this.ws.close();
    }
    this.ws = new WebSocket(this.url, this.protocols);
    this.ws.onopen = () => {
      this.readyState = WebSocket.OPEN;
      this.protocol = this.ws.protocol;
      this.reconnectAttempts = 0;
      this.dispatchEvent(new Event('open'));
      if (reconnecting) {
        this.dispatchEvent(new Event('reconnecting'));
      }
      this.onopen();
    };
    this.ws.onclose = (event) => {
      this.readyState = WebSocket.CLOSED;
      this.protocol = null;
      this.reconnect();
    };
    this.ws.onmessage = (event) => {
      this.dispatchEvent(new Event('message'));
      this.onmessage(event);
    };
    this.ws.onerror = (event) => {
      this.dispatchEvent(new Event('error'));
      this.onerror(event);
    };
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this.ws.send(data);
    } else {
      this.dispatchEvent(new Event('error'));
      this.onerror(new Error('WebSocket is not open'));
    }
  }

  close(code, reason) {
    if (this.ws) {
      this.ws.close(code, reason);
    }
  }

  refresh() {
    this.close();
    this.open(false);
  }

  onopen() {}

  onclose() {}

  onconnecting() {}

  onmessage(event) {}

  onerror(event) {}

  reconnect() {
    if (this.readyState === WebSocket.CLOSED) {
      const reconnectDelay = Math.min(this.maxReconnectInterval, this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts));
      setTimeout(() => {
        this.reconnectAttempts++;
        this.open(true);
        this.dispatchEvent(new Event('connecting'));
        this.onconnecting();
      }, reconnectDelay);
    }
  }

  setup() {
    if (this.automaticOpen) {
      this.open(false);
    }
  }
}
