      headers: headers,
      body: payload
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    return response.arrayBuffer();
  } catch (error) {
    throw new Error(`Failed to convert SSML to speech: ${error.message}`);
  }
}
