const initWebSocket = () => {
  const ws = new WebSocket(window.websocket.connect_url);

  ws.onopen = () => {
    console.log('WebSocket connection established');
    setInterval(() => {
      ws.send(JSON.stringify({ type: 'ping' }));
    }, window.websocket.ping_interval);
  };

  ws.onclose = (event) => {
    console.log('WebSocket connection closed', event);
  };

  ws.onerror = (error) => {
    console.error('WebSocket error', error);
  };

  ws.onmessage = (event) => {
    const message = JSON.parse(event.data);
    switch (message.type) {
      case 'bind':
        bindUser(message.data);
        break;
      case 'message':
        showNewMessage(message.data);
        break;
      case 'login':
        showLoginMessage(message.data);
        break;
      default:
        console.log('Received unknown message type:', message.type);
    }
  };
};

window.onload = initWebSocket;
