```javascript
class WebSocketStore {
  constructor(snack, currentUser) {
    this.wsActive = false;
    this.ws = null;
  }

  listen(callback) {
    const url = `wss://example.com/socket?user=${currentUser.id}`;
    this.ws = new WebSocket(url);

    this.ws.onopen = () => {
      this.wsActive = true;
      snack.notify('WebSocket connected');
    };

    this.ws.onmessage = (event) => {
      const msg = JSON.parse(event.data);
      callback(msg);
    };

    this.ws.onerror = (error) => {
      snack.notify('WebSocket error: ' + error.message);
      this.wsActive = false;
      this.reconnect();
    };

    this.ws.onclose = () => {
      this.wsActive = false;
      snack.notify('WebSocket disconnected');
      this.reconnect();
    };
  }

  close() {
    if (this.ws) {
      this.ws.close(1000);
    }
  }

  reconnect() {
    if (!this.wsActive) {
      setTimeout(() => {
        this.listen();
      }, 5000);
    }
  }
}
