```javascript
const _ = require('lodash');
const JSON = require('json');
const utils = {
  generateUUID: () => 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = (Math.random() * 16) | 0, v = c === 'x' ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  })
};
const alerts = {
  notify: (message) => console.log(message)
};
const WebSocketRPC = function() {
  this._ = _;
  this.JSON = JSON;
  this.utils = utils;
  this.alerts = alerts;
  this.initialized = false;
  this.handles = [];
  this.sock = null;
  this.conf = null;
  this.scheme = '';
  this.onready = null;
};

WebSocketRPC.prototype.onerror = function(ev) {
  this.alerts.notify('WebSocket Error');
  this.sock = null;
  this.initialized = false;
  this.handles.forEach(handle => handle.reject(new Error('WebSocket Error')));
  this.handles = [];
};

WebSocketRPC.prototype.onclose = function(ev) {
  this.alerts.notify('WebSocket Connection Closed');
  this.sock = null;
  this.initialized = false;
  this.handles.forEach(handle => handle.reject(new Error('WebSocket Connection Closed')));
  this.handles = [];
};

WebSocketRPC.prototype.onopen = function() {
  this.alerts.notify('WebSocket Connection Opened');
  this.initialized = true;
  if (this.onready) {
    this.onready();
  }
};

WebSocketRPC.prototype.onmessage = function(message) {
  try {
    const data = this.JSON.parse(message.data);
    const handle = this.handles.find(h => h.id === data.id);
    if (handle) {
      if (data.error) {
        handle.reject(new Error(data.error.message));
      } else {
        handle.resolve(data.result);
      }
      this.handles = this.handles.filter(h => h !== handle);
    }
  } catch (e) {
    this.alerts.notify('Error parsing message');
  }
};

WebSocketRPC.prototype.invoke = function(opts) {
  const id = this.utils.generateUUID();
  const request = {
    jsonrpc: '2.0',
    method: opts.name,
    params: opts.params,
    id: id
  };
  const handle = {
    resolve: null,
    reject: null,
    id: id
  };
  this.handles.push(handle);
  const payload = this.JSON.stringify(request);
  if (this.sock) {
    this.sock.send(payload);
  } else {
    this.alerts.notify('WebSocket not connected');
  }
  return new Promise((resolve, reject) => {
    handle.resolve = resolve;
    handle.reject = reject;
  });
};

WebSocketRPC.prototype.init = function(conf, onready) {
  this.conf = conf;
  this.onready = onready;
  this.scheme = conf.encrypt ? 'wss' : 'ws';
  const url = `${this.scheme}://${conf.host}:${conf.port}${conf.path || ''}`;
  this.sock = new WebSocket(url);
  this.sock.onerror = this.onerror.bind(this);
  this.sock.onclose = this.onclose.bind(this);
  this.sock.onopen = this.onopen.bind(this);
  this.sock.onmessage = this.onmessage.bind(this);
};
