@injectable()
export class MonitorManagerProxyClientImpl implements MonitorManagerProxyClient {
  private webSocket?: WebSocket;
  private wsPort?: number;

  async connect(addressPort: number): Promise<void> {
    if (this.webSocket) {
      if (this.wsPort === addressPort) {
        return;
      }
      this.disconnect();
    }
    try {
      this.webSocket = new WebSocket(`ws://localhost:${addressPort}`);
    } catch {
      throw new Error('Unable to connect to websocket');
    }

    const opened = new Deferred<void>();
    this.webSocket.onopen = () => opened.resolve();
    this.webSocket.onerror = () => opened.reject();
    this.webSocket.onmessage = (message) => {
      const parsedMessage = JSON.parse(message.data);
      if (Array.isArray(parsedMessage)) {
        this.onMessagesReceivedEmitter.fire({ messages: parsedMessage });
      } else if (
        parsedMessage.command === Monitor.MiddlewareCommand.ON_SETTINGS_DID_CHANGE
      ) {
        this.onMonitorSettingsDidChangeEmitter.fire(parsedMessage.data);
      }
    };
    this.wsPort = addressPort;
    return opened.promise;
  }

  disconnect(): void {
    if (!this.webSocket) {
      return;
    }
    try {
      this.webSocket.close();
      this.webSocket = undefined;
    } catch (err) {
      console.error('Could not close the websocket connection for the monitor.', err);
      throw new Error('Unable to close websocket');
    }
  }

  send(message: string): void {
    if (!this.webSocket) {
      return;
    }
    this.webSocket.send(
      JSON.stringify({
        command: Monitor.ClientCommand.SEND_MESSAGE,
        data: message,
      })
    );
  }

  changeSettings(settings: MonitorSettings): void {
    if (!this.webSocket) {
      return;
    }
    this.webSocket.send(
      JSON.stringify({
        command: Monitor.ClientCommand.CHANGE_SETTINGS,
        data: settings,
      })
    );
  }

  async isWSConnected(): Promise<boolean> {
    return !!this.webSocket;
  }

  getWebSocketPort(): number | undefined {
    return this.wsPort;
  }
}

