  const { host, port, language, task, modelSize, useVad } = data;
  const ws = new WebSocket(`wss://${host}:${port}`);
  let isServerReady = false;
  let isCapturing = false;

  ws.onopen = () => {
    isServerReady = true;
    browser.runtime.sendMessage({ type: 'server-ready' });
    ws.send(JSON.stringify({ task, language, modelSize, useVad }));
  };

  ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    if (data.type === 'transcription') {
      browser.runtime.sendMessage({ type: 'transcription', text: data.text });
    } else if (data.type === 'status') {
      browser.runtime.sendMessage({ type: 'status', message: data.message });
    }
  };

  ws.onerror = (error) => {
    browser.runtime.sendMessage({ type: 'error', message: error.message });
  };

  ws.onclose = () => {
    isServerReady = false;
    browser.runtime.sendMessage({ type: 'disconnected' });
  };

  navigator.mediaDevices.getUserMedia({ audio: true })
    .then((stream) => {
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      const source = audioContext.createMediaStreamSource(stream);
      const processor = audioContext.createScriptProcessor(4096, 1, 1);
      source.connect(processor);
      processor.connect(audioContext.destination);

      processor.onaudioprocess = (event) => {
        if (isServerReady && isCapturing) {
          const audioBuffer = event.inputBuffer;
          const audioData = new Float32Array(audioBuffer.length);
          for (let i = 0; i < audioBuffer.length; i++) {
            audioData[i] = audioBuffer.getChannelData(0)[i];
          }
          ws.send(audioData);
        }
      };

      isCapturing = true;
    })
    .catch((error) => {
      browser.runtime.sendMessage({ type: 'error', message: error.message });
