const startRecording = (data) => {
  const { host, port, language, task, modelSize, useVad } = data;
  const ws = new WebSocket(`wss://${host}:${port}`);
  let isServerReady = false;
  let isCapturing = false;
  let audioContext;
  let audioStream;
  let audioProcessor;

  ws.onopen = () => {
    isServerReady = true;
    browser.runtime.sendMessage({ type: 'server-ready' });
    if (useVad) {
      ws.send(JSON.stringify({ type: 'vad', enabled: true }));
    }
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        audioContext = new (window.AudioContext || window.webkitAudioContext)();
        audioStream = audioContext.createMediaStreamSource(stream);
        audioProcessor = audioContext.createScriptProcessor(4096, 1, 1);
        audioStream.connect(audioProcessor);
        audioProcessor.connect(audioContext.destination);
        audioProcessor.onaudioprocess = (event) => {
          if (isCapturing) {
            const audioData = new Float32Array(event.inputBuffer.getChannelData(0));
            const buffer = new ArrayBuffer(audioData.length * 2);
            const dataView = new DataView(buffer);
            for (let i = 0; i < audioData.length; i++) {
              dataView.setInt16(i * 2, audioData[i] * 32767, true);
            }
            ws.send(new Uint8Array(buffer));
          }
        };
      })
      .catch((error) => {
        browser.runtime.sendMessage({ type: 'error', message: error.message });
      });
  };

  ws.onmessage = (event) => {
    const data = JSON.parse(event.data);
    if (data.type === 'transcription') {
      browser.runtime.sendMessage({ type: 'transcription', text: data.text });
    } else if (data.type === 'status') {
      browser.runtime.sendMessage({ type: 'status', message: data.message });
    }
  };

  ws.onerror = (error) => {
    browser.runtime.sendMessage({ type: 'error', message: error.message });
  };

  ws.onclose = () => {
    isServerReady = false;
    isCapturing = false;
    browser.runtime.sendMessage({ type: 'server-closed' });
  };

  window.addEventListener('beforeunload', () => {
    ws.close();
  });

  return {
    start: () => {
      if (isServerReady) {
        isCapturing = true;
        ws.send(JSON.stringify({ type: 'start', task, language, modelSize }));
      }
    },
    stop: () => {
      isCapturing = false;
      ws.send(JSON.stringify({ type: 'stop' }));
    }
  };
};
