```javascript
const startRecording = (data) => {
  const { host, port, language, task, modelSize, useVad } = data;
  const ws = new WebSocket(`wss://${host}:${port}`);
  let isServerReady = false;
  let isCapturing = false;
  let audioContext;
  let audioStream;
  let audioProcessor;

  ws.onopen = () => {
    isServerReady = true;
    browser.runtime.sendMessage({ type: 'server-ready' });
    requestMicrophonePermission();
  };

  ws.onmessage = (event) => {
    const { type, data } = JSON.parse(event.data);
    switch (type) {
      case 'transcription':
        browser.runtime.sendMessage({ type: 'transcription-result', data });
        break;
      case 'status':
        browser.runtime.sendMessage({ type: 'status-update', data });
        break;
      default:
        console.error('Unknown message type:', type);
    }
  };

  ws.onerror = (error) => {
    console.error('WebSocket error:', error);
    browser.runtime.sendMessage({ type: 'websocket-error', error });
  };

  ws.onclose = () => {
    console.log('WebSocket connection closed');
    browser.runtime.sendMessage({ type: 'websocket-closed' });
  };

  const requestMicrophonePermission = () => {
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then((stream) => {
        audioContext = new (window.AudioContext || window.webkitAudioContext)();
        audioStream = stream;
        audioProcessor = audioContext.createScriptProcessor(4096, 1, 1);
        audioProcessor.onaudioprocess = (event) => {
          if (isCapturing) {
            const inputBuffer = event.inputBuffer;
            const audioData = new Float32Array(inputBuffer.length);
            for (let i = 0; i < inputBuffer.length; i++) {
              audioData[i] = inputBuffer.getChannelData(0)[i];
            }
            const audioBlob = new Blob([audioData], { type: 'audio/wav' });
            const audioFile = new File([audioBlob], 'audio.wav');
            const formData = new FormData();
            formData.append('file', audioFile);
            formData.append('language', language);
            formData.append('task', task);
            formData.append('modelSize', modelSize);
            formData.append('useVad', useVad);
            fetch('https://api.transcriptionserver.com/stream', {
              method: 'POST',
              body: formData,
            }).then((response) => response.json())
              .then((data) => {
                ws.send(JSON.stringify({ type: 'transcription', data }));
              })
              .catch((error) => {
                console.error('Error sending audio data:', error);
              });
          }
        };
        audioProcessor.connect(audioContext.destination);
        audioStream.getTracks()[0].connect(audioProcessor);
        isCapturing = true;
      })
      .catch((error) => {
        console.error('Error accessing microphone:', error);
        browser.runtime.sendMessage({ type: 'microphone-error', error });
      });
  };

  const stopRecording = () => {
    if (audioProcessor) {
      audioProcessor.disconnect();
      audioStream.getTracks()[0].disconnect();
      audioProcessor = null;
      audioStream = null;
      audioContext.close();
      audioContext = null;
      isCapturing = false;
    }
  };

  window.addEventListener('beforeunload', stopRecording);
};
