const WebSocket = window.WebSocket;

class PythonWebSocket {
  constructor() {
    this.running = false;
    this.restarting = false;
    this.consoleData = [];
    this.wsOpen = false;
    this.ws = null;
    this.lineno = {
      offset: 0,
      current: -1,
    };
  }

  initPythonWebSocket() {
    this.running = false;
    this.restarting = false;

    this.ws = new WebSocket("ws://" + location.host + "/ws/v1/python");
    this.ws.onopen = () => {
      this.wsOpen = true;
      this.resetConsole();
      console.log("websocket opened");
    };
    this.ws.onmessage = (message) => {
      const data = JSON.parse(message.data);
      switch (data.method) {
        case "gotoLine":
          let lineNumber = data.value + this.lineno.offset;
          this.setLineGoThrough(this.lineno.current);
          this.lineno.current = lineNumber;
          this.editor.session.setBreakpoint(lineNumber);
          break;
        case "resetContent":
          this.editor.setValue(data.value);
          break;
        case "output":
          this.appendConsole(data.value);
          break;
        case "finish":
          this.setLineGoThrough(this.lineno.current);
          this.running = false;
          let timeUsed = (data.value / 1000) + "s";
          this.appendConsole("[Finished " + timeUsed + "]");
          break;
        case "restarted":
          this.restarting = false;
          this.running = false;
          this.resetEditor();
          this.notifySuccess("重启内核", "成功");
          this.runPython(this.generatePreloadCode());
          break;
        default:
          console.error("Unknown method", data.method);
      }
    };
    this.ws.onclose = () => {
      this.wsOpen = false;
      this.ws = null;
      this.running = false;
      this.resetEditor();
      console.log("websocket closed");
    };
  }

  runPython(code) {
    return new Promise((resolve, reject) => {
      this.resetConsole();
      this.resetEditor();
      this.running = true;
      this.ws.send(JSON.stringify({ method: "input", value: code }));
      resolve();
    });
  }

  restartKernel() {
    this.ws.send(JSON.stringify({ method: "restartKernel" }));
    this.restarting = true;
    setTimeout(() => {
      this.restarting = false;
    }, 500);
  }

  stopDebugging() {
    this.ws.send(JSON.stringify({ method: "keyboardInterrupt" }));
  }

  appendConsole(text) {
    this.consoleData.push({ lineno: this.lineno.current, value: text });
    setTimeout(() => {
      let c = this.$refs.console;
      c.scrollTop = c.scrollHeight - c.clientHeight;
    }, 1);
  }

  resetConsole() {
    this.consoleData = [];
  }

  resetEditor() {
    this.editor.session.clearBreakpoints();
    this.lineno.current = -1;
  }

  setLineGoThrough(lineno) {
    if (lineno >= 0) {
      this.editor.session.setBreakpoint(lineno, "ace_code_exercised");
    }
  }
}

