import { isChildClass, isNull, isUndefined } from '@polkadot/util';
import { xglobal } from '@polkadot/x-global';
import { WebSocket } from '@polkadot/x-ws';

class WsProvider {
  #websocket: WebSocket | null;

  constructor(endpoint: string) {
    this.#websocket = null;
    this.#connect(endpoint);
  }

  #connect(endpoint: string): void {
    try {
      this.#websocket = typeof xglobal.WebSocket !== 'undefined' && isChildClass(xglobal.WebSocket as unknown as Class<WebSocket>, WebSocket)
        ? new WebSocket(endpoint)
        : new WebSocket(endpoint, undefined, {});

      if (this.#websocket) {
        this.#websocket.onclose = this.#onSocketClose;
        this.#websocket.onerror = this.#onSocketError;
        this.#websocket.onmessage = this.#onSocketMessage;
        this.#websocket.onopen = this.#onSocketOpen;
      }
    } catch (error) {
      throw error;
    }
  }

  #onSocketClose(event: CloseEvent): void {
    this.#websocket = null;
  }

  #onSocketError(error: Event): void {
    // Handle error
  }

  #onSocketMessage(message: MessageEvent<string>): void {
    const response = JSON.parse(message.data) as JsonRpcResponse<string>;
    // Process message
  }

  #onSocketOpen(): boolean {
    return true;
  }

  public send(body: string): void {
    if (!this.#websocket) {
      throw new Error('WebSocket is not connected');
    }
    this.#websocket.send(body);
  }

  public disconnect(): void {
    if (this.#websocket) {
      this.#websocket.close(1000);
    }
  }
}

