        options
      )
      .then(response => response.data)
      .catch(console.error)
    const subscribedChannels = await axios
      .post(
        `https://servicebuss.transvision.co.id/tvs/subscribe_product/list?platformId=${config.platformId}`,
        options
      )
      .then(response => response.data)
