var WebSocket = require('ws');
var EventEmitter = require('eventemitter3');
var uuid = require('uuid');

class WebSocketServer {
  constructor(options) {
    this.options = options;
    this.wss = null;
  }

  createServer() {
    this.wss = new WebSocket.Server({
      port: this.options.publicPort,
      perMessageDeflate: false
    });

    var listeningListener, errorListener;
    return new Promise((resolve, reject) => {
      listeningListener = () => resolve(this.wss);
      errorListener = (err) => reject(err);

      this.wss.on('listening', listeningListener);
      this.wss.on('error', errorListener);
    })
    .finally(() => {
      this.wss.removeListener('listening', listeningListener);
      this.wss.removeListener('error', errorListener);
    });
  }

  onConnection(callback) {
    this.wss.on('connection', (ws) => {
      var id = uuid.v4();
      callback(ws, id);
    });
  }

  close() {
    this.wss.close();
  }
}

