const WebSocket = require('ws');

class XVIZServer {
  constructor(handlers, options, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('Handlers array cannot be empty');
    }
    this._server = new WebSocket.Server(options);
    this.handlers = handlers;
    this.options = { ...this.defaultOptions, ...options };
    this._server.on('connection', (socket, request) => {
      this.handleSession(socket, request).catch((error) => {
        this.log('Error handling session:', error);
        socket.close();
      });
    });
    if (typeof callback === 'function') {
      callback();
    }
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    for (const handler of this.handlers) {
      if (await handler.accept(socket, request)) {
        return;
      }
    }
    socket.close();
  }

  log(...msg) {
    if (this.options.logger) {
      this.options.logger(...msg);
    }
  }

  static get defaultOptions() {
    return {
      port: 8080,
      logger: console.log
    };
