const Aedes = require('aedes');

const createWebSocketServer = () => {
  const server = new WebSocket.Server({ port: 8080 });
  const aedes = Aedes();
  const client = mqtt.connect('mqtt://localhost');

  server.on('connection', function connection(ws) {
    ws.on('message', function incoming(message) {
      const topic = message.toString().split(' ')[1];
      const payload = message.toString().split(' ')[2];
      client.publish(topic, payload);
    });
  });

  client.on('connect', () => {
    aedes.on('client', (client) => {
      server.clients.forEach((ws) => {
        ws.send(JSON.stringify({
          topic: client.id,
          payload: 'client connected'
        }));
      });
    });

    aedes.on('publish', (packet, client) => {
      server.clients.forEach((ws) => {
        ws.send(JSON.stringify({
          topic: packet.topic,
          payload: packet.payload.toString()
        }));
      });
    });

    aedes.on('subscribe', (packet, client) => {
      server.clients.forEach((ws) => {
        ws.send(JSON.stringify({
          topic: packet.topic,
          payload: 'client subscribed'
        }));
      });
    });

    aedes.on('unsubscribe', (packet, client) => {
      server.clients.forEach((ws) => {
        ws.send(JSON.stringify({
          topic: packet.topic,
          payload: 'client unsubscribed'
        }));
      });
    });

    aedes.on('clientDisconnect', (client) => {
      server.clients.forEach((ws) => {
        ws.send(JSON.stringify({
          topic: client.id,
          payload: 'client disconnected'
        }));
      });
    });
  });

