class SauceLabsClient {
    constructor() {
        this.auth = null;
    }

    setAuth(saucelabsUsername, saucelabsToken) {
        this.auth = {
            username: saucelabsUsername,
            token: saucelabsToken
        };
    }

    get(path, callback) {
        const xhr = new XMLHttpRequest();
        xhr.open('GET', `https://saucelabs.com/rest/v1/${this.auth.username}/${path}`, true);
        xhr.setRequestHeader('Authorization', `Basic ${btoa(`${this.auth.username}:${this.auth.token}`)}`);
        xhr.onreadystatechange = () => {
            if (xhr.readyState === 4) {
                if (xhr.status >= 200 && xhr.status < 300) {
                    callback(null, JSON.parse(xhr.responseText));
                } else {
                    callback(xhr.statusText, null);
                }
            }
        };
        xhr.send();
    }

    post(path, data, callback) {
        const xhr = new XMLHttpRequest();
        xhr.open('POST', `https://saucelabs.com/rest/v1/${this.auth.username}/${path}`, true);
        xhr.setRequestHeader('Authorization', `Basic ${btoa(`${this.auth.username}:${this.auth.token}`)}`);
        xhr.setRequestHeader('Content-Type', 'application/json');
        xhr.onreadystatechange = () => {
            if (xhr.readyState === 4) {
                if (xhr.status >= 200 && xhr.status < 300) {
                    callback(null, JSON.parse(xhr.responseText));
                } else {
                    callback(xhr.statusText, null);
                }
            }
        };
        xhr.send(JSON.stringify(data));
    }
}
