const axios = require('axios');

class BundleSizeAPI {
  constructor(repo, token, sha, ci) {
    this.repo = repo;
    this.token = token;
    this.sha = sha;
    this.ci = ci;
    this.url = 'https://bundlesize-store.now.sh/values';
    this.enabled = (this.repo && this.token) ? true : false;
  }

  get() {
    const sanitizedRepo = this.repo.replace(/\./g, '_');
    return axios
      .get(`${this.url}?repo=${sanitizedRepo}&token=${this.token}`)
      .then(response => {
        const values = {};
        if (response && response.data && response.data.length) {
          response.data.map(file => (values[file.path] = file.size));
        }
        return values;
      })
      .catch(error => {
        console.log(error);
      });
  }

  set(values) {
    if (this.repo && this.token) {
      const sanitizedRepo = this.repo.replace(/\./g, '_');
      axios
        .post(this.url, { repo: sanitizedRepo, token: this.token, sha: this.sha, values })
        .catch(error => console.log(error));
    }
  }
}

