        self.token = token
        self.sha = sha
        self.ci = ci
        self.url = "https://bundlesize-store.now.sh/values"
        self.enabled = bool(repo and token)

    def get(self):
        if not self.enabled:
            return Promise({})

        try:
            response = requests.get(f"{self.url}/{self.repo}/{self.sha}", headers={"Authorization": f"Bearer {self.token}"})
