        if (this._isQuanX) {
            if (typeof options == "string") options = { url: options }
            options["method"] = "GET"
            $task.fetch(options).then(response => { callback(null, this._status(response), response.body) }, reason => callback(reason.error, null, null))
        }
        if (this._isSurge) $httpClient.get(options, (error, response, body) => { callback(error, this._status(response), body) })
        if (this._node) this._node.request(options, (error, response, body) => { callback(error, this._status(response), body) })
    }

    post(options, callback) {
        if (this._isQuanX) {
            if (typeof options == "string") options = { url: options }
            options["method"] = "POST"
            $task.fetch(options).then(response => { callback(null, this._status(response), response.body) }, reason => callback(reason.error, null, null))
        }
        if (this._isSurge) $httpClient.post(options, (error, response, body) => { callback(error, this._status(response), body) })
        if (this._node) this._node.request.post(options, (error, response, body) => { callback(error, this._status(response), body) })
    }

    _status(response) {
        if (response) {
            if (response.status) {
                response["statusCode"] = response.status
            } else if (response.statusCode) {
                response["status"] = response.statusCode
            }
