            $prefs.setValueForKey(key, value);
            return true;
        } else if (this._isQuanX) {
            $prefs.setValueForKey(key, value);
            return true;
        } else if (this._node) {
            try {
                require('fs').writeFileSync(`./${key}.txt`, value);
                return true;
            } catch (error) {
                return false;
            }
        }
        return false;
    }

    read(key) {
        if (this._isSurge) {
            return $prefs.valueForKey(key);
        } else if (this._isQuanX) {
            return $prefs.valueForKey(key);
        } else if (this._node) {
            try {
                return require('fs').readFileSync(`./${key}.txt`, 'utf8');
            } catch (error) {
                return '';
            }
        }
        return '';
    }

    get(options, callback) {
        this.makeRequest('GET', options, callback);
    }

    post(options, callback) {
        this.makeRequest('POST', options, callback);
    }

    _status(response) {
        if (this._isSurge || this._isQuanX) {
            return { status: response.status };
        } else if (this._node) {
            return { status: response.statusCode };
        }
        return { status: 0 };
    }

    makeRequest(method, options, callback) {
        if (this._isSurge) {
            $http(method, options, (response) => {
                callback(this._status(response));
            });
        } else if (this._isQuanX) {
            $http(method, options, (response) => {
                callback(this._status(response));
            });
        } else if (this._node) {
            const http = require('http');
            const options = typeof options === 'string' ? { hostname: options } : options;
            const req = http.request(options, (res) => {
                let data = '';
                res.on('data', (chunk) => {
                    data += chunk;
                });
                res.on('end', () => {
                    callback(this._status({ statusCode: res.statusCode, data: data }));
                });
            });
            req.on('error', (e) => {
                callback({ status: 0 });
            });
            req.end();
        }
    }

    detectEnvironment() {
        if (typeof $prefs !== 'undefined') {
            this._isSurge = true;
        } else if (typeof $notification !== 'undefined') {
            this._isQuanX = true;
        } else if (typeof require !== 'undefined') {
            this._node = require;
        }
    }
}
