class Tool {
    constructor() {
        this._node = null;
        this._isSurge = false;
        this._isQuanX = false;
        this.isSurge = this._isSurge;
        this.isQuanX = this._isQuanX;
        this.isResponse = false;
        this.detectEnvironment();
    }

    notify(title, subtitle, message, option) {
        if (this._isSurge) {
            $notify(title, subtitle, message, option);
        } else if (this._isQuanX) {
            $notification.post(title, subtitle, message, option);
        } else if (this._node) {
            this._node.child_process.exec(`osascript -e 'display notification "${title}" with subtitle "${subtitle}" and message "${message}"'`, (error, stdout, stderr) => {
                if (error) {
                    console.error(`exec error: ${error}`);
                    return;
                }
                if (stderr) {
                    console.error(`stderr: ${stderr}`);
                    return;
                }
                console.log(`stdout: ${stdout}`);
            });
        }
    }

    write(value, key) {
        if (this._isSurge) {
            $prefs.setValueForKey(key, value);
            return true;
        } else if (this._isQuanX) {
            $prefs.setValueForKey(key, value);
            return true;
        } else if (this._node) {
            const fs = require('fs');
            const path = require('path');
            const filePath = path.join(__dirname, 'storage', key);
            fs.writeFileSync(filePath, value);
            return true;
        }
        return false;
    }

    read(key) {
        if (this._isSurge) {
            return $prefs.valueForKey(key);
        } else if (this._isQuanX) {
            return $prefs.valueForKey(key);
        } else if (this._node) {
            const fs = require('fs');
            const path = require('path');
            const filePath = path.join(__dirname, 'storage', key);
            if (fs.existsSync(filePath)) {
                return fs.readFileSync(filePath, 'utf8');
            }
        }
        return null;
    }

    get(options, callback) {
        this.makeRequest('GET', options, callback);
    }

    post(options, callback) {
        this.makeRequest('POST', options, callback);
    }

    _status(response) {
        if (response.statusCode) {
            return { status: response.statusCode };
        }
        return { status: 200 };
    }

    makeRequest(method, options, callback) {
        if (this._isSurge) {
            $http(method, options, (error, response) => {
                if (error) {
                    callback(error, null);
                    return;
                }
                callback(null, response);
            });
        } else if (this._isQuanX) {
            $http(method, options, (error, response) => {
                if (error) {
                    callback(error, null);
                    return;
                }
                callback(null, response);
            });
        } else if (this._node) {
            const http = require('http');
            const options = typeof options === 'string' ? { hostname: options } : options;
            const req = http.request(options, (res) => {
                let data = '';
                res.on('data', (chunk) => {
                    data += chunk;
                });
                res.on('end', () => {
                    callback(null, { statusCode: res.statusCode, data: data });
                });
            });
            req.on('error', (e) => {
                callback(e, null);
            });
            req.end();
        }
    }

    detectEnvironment() {
        if (typeof $prefs !== 'undefined') {
            this._isSurge = true;
            this._isQuanX = false;
        } else if (typeof $notification !== 'undefined') {
            this._isSurge = false;
            this._isQuanX = true;
        } else if (typeof process !== 'undefined' && process.versions && process.versions.node) {
            this._node = true;
            this._isSurge = false;
            this._isQuanX = false;
        }
    }
}
