```javascript
function makeRequest(resolve, config, method, fullUrl, headers, payload, defaultTimeout, xhrExtra, onRequestProgress, onRequestStart, onRequestEnd, onResponseStart, onResponseProgress, onResponseEnd, onTimeoutError, onSuccess, onError, onBeforeRequest, createAbortListener, parseResponse, parseErrorResponse, getResponseHeaders) {
  var xhr = new XMLHttpRequest();
  var timeoutId;
  var timeout = defaultTimeout || 0;
  var abortListener;

  function handleProgress(event) {
    if (event.lengthComputable) {
      var percentComplete = event.loaded / event.total * 100;
      onRequestProgress(percentComplete);
    }
  }

  function handleTimeout() {
    xhr.abort();
    onTimeoutError(xhrExtra);
    resolve({ status: 'timeout', xhr: xhr });
  }

  function handleResponse() {
    if (xhr.status >= 200 && xhr.status < 300) {
      onResponseEnd();
      var responseHeaders = getResponseHeaders(xhr.getAllResponseHeaders());
      var parsedResponse = parseResponse(xhr.responseText, responseHeaders);
      onSuccess(parsedResponse);
      resolve({ status: 'success', xhr: xhr });
    } else {
      onError(xhr.status, xhr.statusText);
      resolve({ status: 'error', xhr: xhr });
    }
  }

  function handleError() {
    onError(xhr.status, xhr.statusText);
    resolve({ status: 'error', xhr: xhr });
  }

  function handleAbort() {
    onRequestEnd();
    onResponseEnd();
    resolve({ status: 'aborted', xhr: xhr });
  }

  function startRequest() {
    if (onBeforeRequest) {
      onBeforeRequest();
    }
    if (onRequestStart) {
      onRequestStart();
    }
    xhr.open(method, fullUrl, true);
    for (var header in headers) {
      xhr.setRequestHeader(header, headers[header]);
    }
    xhr.timeout = timeout;
    xhr.onprogress = handleProgress;
    xhr.ontimeout = handleTimeout;
    xhr.onload = handleResponse;
    xhr.onerror = handleError;
    xhr.onabort = handleAbort;
    if (createAbortListener) {
      abortListener = createAbortListener(xhr);
    }
    if (payload) {
      xhr.send(JSON.stringify(payload));
    } else {
      xhr.send();
    }
  }

  if (timeout > 0) {
    timeoutId = setTimeout(handleTimeout, timeout);
  }

  startRequest();

  return {
    abort: function() {
      xhr.abort();
      clearTimeout(timeoutId);
      if (abortListener) {
        abortListener();
      }
    }
  };
}
