          audioContext.cache[cacheKey] = {
            instrument,
            name,
            status: 'loaded',
            audioBuffer: buffer
          };
          resolve(audioContext.cache[cacheKey]);
        }, (error) => {
          reject(new Error('Audio decoding failed'));
        });
      } else {
        reject(new Error('Failed to load audio file'));
      }
    };

    xhr.onerror = () => {
      reject(new Error('Network error'));
    };

    xhr.send();
  });
}

function getNote(url, instrument, name, audioContext) {
  return new Promise((resolve, reject) => {
    const cacheKey = `${instrument}-${name}`;
    if (audioContext.cache[cacheKey]) {
      resolve(audioContext.cache[cacheKey]);
      return;
    }

    const xhr = new XMLHttpRequest();
    xhr.open('GET', `${url}/${instrument}/${name}.mp3`, true);
    xhr.responseType = 'arraybuffer';

    xhr.onload = () => {
      if (xhr.status === 200) {
        audioContext.decodeAudioData(xhr.response, (buffer) => {
          audioContext.cache[cacheKey] = {
            instrument,
            name,
            status: 'loaded',
            audioBuffer: buffer
          };
          resolve(audioContext.cache[cacheKey]);
        }, (error) => {
          reject(new Error('Audio decoding failed'));
        });
      } else {
        reject(new Error('Failed to load audio file'));
      }
    };

    xhr.onerror = () => {
      reject(new Error('Network error'));
    };

    xhr.send();
  });
}

