      206: 200,
      301: 200,
      302: 200,
      303: 200,
      304: 200,
      307: 200,
      308: 200
    };
    this.xhrSupported = typeof XMLHttpRequest !== 'undefined' ? new XMLHttpRequest() : false;
  }

  createXHR() {
    if (this.xhrSupported) {
      return new XMLHttpRequest();
    }
    return undefined;
  }

