import requests
from urllib.parse import urlparse, parse_qs, urljoin
import time

class URLExpander:
    def __init__(self):
        self.map = {
            'is.gd': 'is.gd',
            'goo.gl': 'goo.gl',
            'budurl.com': 'budurl.com',
            'snipurl.com': 'snipurl.com'
        }
        self.timeout = 5000
        self.APIs = {
            'is.gd': self._expand_is_gd,
            'goo.gl': self._expand_goo_gl,
            'budurl.com': self._expand_budurl,
            'snipurl.com': self._expand_snipurl
        }

    def expand(self, args, callback):
        url = args.get('url')
        depth = args.get('depth', 0)
        seen = args.get('seen', {})

        if url in seen:
            callback(args, None)
            return

        seen[url] = True

        parsed_url = urlparse(url)
        domain = parsed_url.netloc

        if domain in self.map:
            expanded_url = self.APIs[domain](url)
            if expanded_url:
                args['url'] = expanded_url
                args['depth'] = depth + 1
                self.expand(args, callback)
            else:
                callback(args, None)
        else:
            self._follow_redirects(url, depth, seen, callback)

    def _expand_is_gd(self, url):
        response = requests.get(url, timeout=self.timeout)
        if response.status_code == 200:
            return response.url
        return None

    def _expand_goo_gl(self, url):
        response = requests.get(url, timeout=self.timeout)
        if response.status_code == 200:
            return response.url
        return None

    def _expand_budurl(self, url):
        response = requests.get(url, timeout=self.timeout)
        if response.status_code == 200:
            return response.url
        return None

    def _expand_snipurl(self, url):
        response = requests.get(url, timeout=self.timeout)
        if response.status_code == 200:
            return response.url
        return None

    def _follow_redirects(self, url, depth, seen, callback):
        if depth > 5:
            callback({'url': url, 'depth': depth, 'seen': seen}, None)
            return

        response = requests.get(url, timeout=self.timeout)
        if response.status_code == 301 or response.status_code == 302:
            new_url = response.headers['Location']
            new_url = urljoin(url, new_url)
            args = {'url': new_url, 'depth': depth + 1, 'seen': seen}
            self.expand(args, callback)
        else:
            callback({'url': url, 'depth': depth, 'seen': seen}, None)
