import requests
import re
from urllib.parse import urlparse, parse_qs

class URLExpander:
    def __init__(self):
        self.map = {
            'isgd': r'^https?://(?:www\.)?is\.gd/([a-zA-Z0-9]+)',
            'googl': r'^https?://(?:www\.)?goo\.gl/([a-zA-Z0-9]+)',
            'budurl': r'^https?://(?:www\.)?budurl\.com/([a-zA-Z0-9]+)',
            'snipurl': r'^https?://(?:www\.)?snipurl\.com/([a-zA-Z0-9]+)'
        }
        self.timeout = 5000
        self.APIs = {
            'isgd': self._isgd,
            'googl': self._googl,
            'budurl': self._budurl,
            'snipurl': self._snipurl,
            'generic': self._generic
        }

    def expand(self, args, callback):
        url = args.get('url')
        depth = args.get('depth', 0)
        seen = args.get('seen', {})
        if depth > 5:
            callback({'url': url, 'err': 'Maximum depth exceeded', 'depth': depth, 'seen': seen})
            return
        if url in seen:
            callback({'url': url, 'err': 'Cycle detected', 'depth': depth, 'seen': seen})
            return
        seen[url] = True

        def handle_response(response):
            if response.status_code == 301 or response.status_code == 302:
                new_url = response.headers.get('Location')
                args['url'] = new_url
                self.expand(args, callback)
            else:
                callback({'url': url, 'err': 'Unexpected status code', 'depth': depth, 'seen': seen})

        for service, pattern in self.map.items():
            match = re.match(pattern, url)
            if match:
                api_method = self.APIs.get(service)
                if api_method:
                    api_method(match.group(1), args, callback)
                    return
                break
        self._generic(url, args, handle_response)

    def _isgd(self, key, args, callback):
        response = requests.get(f'https://is.gd/{key}', timeout=self.timeout)
        self.expand(args, callback)

    def _googl(self, key, args, callback):
        response = requests.get(f'https://goo.gl/{key}', timeout=self.timeout)
        self.expand(args, callback)

    def _budurl(self, key, args, callback):
        response = requests.get(f'https://budurl.com/{key}', timeout=self.timeout)
        self.expand(args, callback)

    def _snipurl(self, key, args, callback):
        response = requests.get(f'https://snipurl.com/{key}', timeout=self.timeout)
        self.expand(args, callback)

    def _generic(self, url, args, callback):
        response = requests.head(url, allow_redirects=True, timeout=self.timeout)
        handle_response(response)
