  console.log(`Server running on port ${port}`);
});

const shutdown = () => {
  server.close(() => {
    process.exit(0);
  });
};

const gracefulShutdown = () => {
  const d = domain.create();
  d.on('error', (err) => {
    console.error('Error:', err);
    shutdown();
  });
  d.run(() => {
    if (cluster.isMaster) {
      for (const worker of Object.values(cluster.workers)) {
        worker.on('exit', (code, signal) => {
          console.log(`worker ${worker.process.pid} died`);
        });
      }
    }
  });
};

if (cluster.isMaster) {
  const cpuCount = os.cpus().length;
  for (let i = 0; i < cpuCount; i++) {
    cluster.fork();
  }
} else {
  gracefulShutdown();
}

const express = require('express');
const bodyParser = require('body-parser');
const rest = require('connect-rest');
const Attraction = require('./models/Attraction');
const cluster = require('cluster');
const os = require('os');
const domain = require('domain');

const app = express();
const port = process.env.PORT || 3000;

app.use(bodyParser.json());

const routes = rest({
  attractions: {
    get: { path: '/attractions', handler: getAttractions },
    post: { path: '/attraction', handler: addAttraction }
  },
  attraction: {
    get: { path: '/attraction/:id', handler: getAttraction }
  }
});

app.use('/api', routes);

function getAttractions(req, res) {
  Attraction.find({ approved: true }, (err, attractions) => {
    if (err) {
      return handleError(res, err);
    }
    return res.status(200).json(attractions);
  });
}

function addAttraction(req, res) {
  const newAttraction = new Attraction(req.body);
  newAttraction.save((err, attraction) => {
    if (err) {
      return handleError(res, err);
    }
    return res.status(201).json({ id: attraction._id });
  });
}

function getAttraction(req, res) {
  Attraction.findById(req.params.id, (err, attraction) => {
    if (err) {
      return handleError(res, err);
    }
    if (!attraction) {
      return res.status(404).send('Not Found');
    }
    return res.status(200).json(attraction);
  });
}

function handleError(res, err) {
  const domainErr = domain.create();
  domainErr.on('error', () => {
    console.error(err.stack);
    res.status(500).send('Server Error');
    process.exit(1);
  });
  domainErr.run(() => {
    if (err) {
      return handleError(res, err);
    }
  });
}

if (cluster.isMaster) {
  const numCPUs = os.cpus().length;
  console.log(`Master ${process.pid} is running`);

  for (let i = 0; i < numCPUs; i++) {
    cluster.fork();
  }

  cluster.on('exit', (worker, code, signal) => {
    console.log(`worker ${worker.process.pid} died`);
  });
} else {
  app.listen(port, () => {
    console.log(`Server running on port ${port}`);
  });
}

