function setupServer() {
    var express = require('express');
    var app = express();

    app.use(function(req, res, next) {
        var setHeader = res.setHeader;
        res.setHeader = function(name) {
            switch (name) {
                case 'Cache-Control':
                case 'Last-Modified':
                case 'ETag':
                    return;
            }
            return setHeader.apply(res, arguments);
        };
        next();
    });

