const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

function setupExpressServer() {
    const app = express();
    const PORT = 3000;
    const filePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

    app.use(responseTime());

    app.get('/non-streaming', (req, res) => {
        fs.readFile(filePath, (err, data) => {
            if (err) {
                return res.status(500).send('Error reading file');
            }
            res.setHeader('Content-Type', 'image/jpeg');
            res.send(data);
        });
    });

    app.get('/streaming', (req, res) => {
        const readStream = fs.createReadStream(filePath);
        res.setHeader('Content-Type', 'image/jpeg');
        readStream.pipe(res);
    });

    app.get('/non-streaming-buffer', (req, res) => {
        fs.readFile(filePath, (err, data) => {
            if (err) {
                return res.status(500).send('Error reading file');
            }
            res.setHeader('Content-Type', 'image/jpeg');
            res.send(data);
        });
    });

    app.get('/streaming-chunked', (req, res) => {
        const readStream = fs.createReadStream(filePath, { highWaterMark: 16 * 1024 });
        res.setHeader('Content-Type', 'image/jpeg');
        readStream.pipe(res);
    });

    app.listen(PORT, () => {
        console.log(`Server is running on http://localhost:${PORT}`);
    });
}

