        windowMs: 1000, // 1 second
        max: 5 // limit each IP to 5 requests per windowMs
    });

    app.use(cors());
    app.use(limiter);
    app.use(express.json());

    app.get('/query', async (req, res) => {
        const { url, selector } = req.query;
        if (!url || !selector) return res.status(400).send('url and selector query parameters are required');

        try {
            const browser = await launch();
            const page = await browser.newPage();
            await page.goto(url);
            const elements = await page.$$eval(selector, elems => elems.map(e => ({
                text: e.innerText,
                href: e.href
            })));
            await browser.close();
            res.json(elements);
        } catch (error) {
            res.status(500).send('Error querying elements');
        }
    });

    app.get('/fullHtml', async (req, res) => {
        const { url } = req.query;
        if (!url) return res.status(400).send('url query parameter is required');

        try {
            const browser = await launch();
            const page = await browser.newPage();
            await page.goto(url);
            const content = await page.content();
            await browser.close();
            res.send(content);
        } catch (error) {
            res.status(500).send('Error retrieving full HTML');
        }
