class Server {
    constructor(config) {
        this.config = config;
        this._http = require('http').createServer();
        this.sockets = [];
        this.path = config.path;
        this.updateServer();
    }

    updateServer() {
        const fs = require('fs');
        const path = require('path');
        const send = require('send');

        this._http.on('request', (req, res) => {
            const filePath = path.join(this.path, req.url === '/' ? 'index.html' : req.url);
            send(req, filePath, { root: this.path })
                .on('error', (err) => {
                    if (err.status === 404) {
                        this.handleNotFound(req, res);
                    } else {
                        res.writeHead(500);
                        res.end('Internal Server Error');
                    }
                })
                .pipe(res);
        });

        this._http.on('connection', (socket) => {
            this.sockets.push(socket);
            socket.on('close', () => {
                this.sockets = this.sockets.filter(s => s !== socket);
            });
        });
    }

    start(callback) {
        this._http.listen(this.config.port, () => {
            console.log(`Server running at http://localhost:${this.config.port}/`);
            if (callback) callback();
        });
    }

    stop(callback) {
        this._http.close(() => {
            this.sockets.forEach(socket => socket.destroy());
            this.sockets = [];
            console.log('Server stopped.');
            if (callback) callback();
        });
    }

    handleNotFound(req, res) {
        const dirPath = path.join(this.path, req.url);
        fs.stat(dirPath, (err, stats) => {
            if (err || !stats.isDirectory()) {
                res.writeHead(404);
                res.end('404 Not Found');
            } else {
                fs.readdir(dirPath, (err, files) => {
                    if (err) {
                        res.writeHead(500);
                        res.end('Internal Server Error');
                    } else {
                        res.writeHead(200, { 'Content-Type': 'text/html' });
                        res.end(`<h1>Directory Listing</h1><ul>${files.map(file => `<li><a href="${path.join(req.url, file)}">${file}</a></li>`).join('')}</ul>`);
                    }
                });
            }
        });
    }
}

