const http = require('http');
const fs = require('fs');
const path = require('path');

const staticDirs = [path.join(__dirname, '../src'), path.join(__dirname, '../dist')];
const fileCache = new Map();

function getContentType(ext) {
    const types = {
        '.html': 'text/html',
        '.js': 'application/javascript',
        '.css': 'text/css',
        '.json': 'application/json',
        '.png': 'image/png',
        '.jpg': 'image/jpeg',
        '.gif': 'image/gif',
        '.svg': 'image/svg+xml',
        '.woff': 'font/woff',
        '.woff2': 'font/woff2',
        '.otf': 'font/otf',
        '.ttf': 'font/ttf',
        '.pdf': 'application/pdf',
    };
    return types[ext] || 'application/octet-stream';
}

function cacheFiles() {
    staticDirs.forEach(dir => {
        fs.readdirSync(dir).forEach(file => {
            const filePath = path.join(dir, file);
            const stat = fs.statSync(filePath);
            if (stat.isFile()) {
                fileCache.set(file, filePath);
            }
        });
    });
    console.log('Loaded files:', Array.from(fileCache.keys()));
}

function createServer(port = 90) {
    cacheFiles();

    const server = http.createServer((req, res) => {
        const requestedPath = req.url === '/' ? '/index.html' : req.url;
        const fileName = requestedPath.split('/').pop();
        const filePath = fileCache.get(fileName);

        if (filePath) {
            const ext = path.extname(filePath);
            res.writeHead(200, { 'Content-Type': getContentType(ext) });
            const readStream = fs.createReadStream(filePath);
            readStream.pipe(res);
        } else {
            res.writeHead(404, { 'Content-Type': 'text/plain' });
            res.end('404 Not Found');
        }
    });

    server.listen(port, '127.0.0.1', () => {
        console.log(`Server running at http://127.0.0.1:${port}/`);
    });
}

const portArg = process.argv[2];
createServer(portArg);

