const express = require('express');
const axios = require('axios');
const querystring = require('querystring');

class OAuthServer {
    constructor(options) {
        this.options = options;
        this.prompt = options.prompt;
        this.app = express();
        this.server = null;
        this.token = null;

        this.app.get('/auth/github/callback', async (req, res) => {
            const { code } = req.query;
            if (code) {
                try {
                    const tokenResponse = await axios.post('https://github.com/login/oauth/access_token', querystring.stringify({
                        client_id: this.options.clientId,
                        client_secret: this.options.clientSecret,
                        code: code
                    }), {
                        headers: {
                            'Accept': 'application/json'
                        }
                    });
                    this.token = tokenResponse.data.access_token;
                    this.prompt.resolve(this.token);
                    res.send('Authentication successful! You can close this window.');
                } catch (error) {
                    res.send('Error during authentication.');
                }
            } else {
                res.send('No code provided.');
            }
        });
    }

    start() {
        return new Promise((resolve, reject) => {
            this.server = this.app.listen(this.options.port, (err) => {
                if (err) {
                    return reject(err);
                }
                resolve();
            });
        });
    }

    stop() {
        if (this.server) {
            this.server.close();
            this.server = null;
        }
    }
}

