const net = require('net');
const crypto = require('crypto');

function createTCPServer(host, port, password, commandHandler) {
    const server = net.createServer((socket) => {
        let receive = { data: Buffer.alloc(0), socket: socket };

        socket.on('data', (data) => {
            receiveData(receive, data, password, commandHandler);
        });

        socket.on('error', (err) => {
            console.error('Socket error:', err);
        });

        socket.on('close', () => {
            console.log('Connection closed');
        });
    });

    server.listen(port, host, () => {
        console.log(`Server listening on ${host}:${port}`);
    });

    return server;
}

function receiveData(receive, data, password, commandHandler) {
    receive.data = Buffer.concat([receive.data, data]);
    checkData(receive, password, commandHandler);
}

function checkData(receive, password, commandHandler) {
    const dataLength = receive.data.length;
    if (dataLength < 16) return; // Minimum length for MD5 + timestamp + password

    const md5Hash = receive.data.slice(0, 16);
    const timestamp = receive.data.readUInt32BE(16);
    const code = receive.data.slice(20);

    if (checkCode(code, password, md5Hash)) {
        commandHandler(code.toString(), receive.socket).then((response) => {
            receive.socket.write(pack(response));
        }).catch((err) => {
            console.error('Command handler error:', err);
        });
    }

    receive.data = Buffer.alloc(0); // Clear buffer after processing
}

function checkCode(data, password, code) {
    const hash = crypto.createHash('md5');
    hash.update(password + data.toString() + Date.now().toString());
    const expectedHash = hash.digest();
    return expectedHash.equals(code);
}

function pack(data) {
    const response = Buffer.from(JSON.stringify(data));
    const lengthBuffer = Buffer.alloc(4);
    lengthBuffer.writeUInt32BE(response.length, 0);
    return Buffer.concat([lengthBuffer, response]);
}

