		socket.destroy();

		this.sockets.splice(this.sockets.indexOf(socket), 1);
	}

	close() {
		if (this.server && this.server.listening) {
			this.server.close();

			this.sockets.forEach(socket => socket.destroy());
			this.sockets = [];
		}
	}
}

