            this.server.listen(this.opts.port, (err) => {
                if (err) {
                    this.logger.error('Error starting server:', err);
                    return reject(err);
                }
                this.connected = true;
                this.logger.info(`Server listening on port ${this.opts.port}`);
                resolve();
            });
        });
    }

    onTcpClientConnected(socket) {
        this.sockets.push(socket);
        this.logger.info('New client connected:', socket.remoteAddress);
        socket.on('data', (data) => this.handleData(socket, data));
        socket.on('error', (err) => this.closeSocket(socket, err));
        socket.on('end', () => this.closeSocket(socket));
    }

    handleData(socket, data) {
        // Custom parser logic can be implemented here
        this.transporter.handleMessage(data.toString(), socket);
    }

    closeSocket(socket, err) {
        if (err) {
            this.logger.error('Socket error:', err);
        }
        socket.end();
        this.sockets = this.sockets.filter(s => s !== socket);
        this.logger.info('Client disconnected:', socket.remoteAddress);
    }

    close() {
        this.sockets.forEach(socket => this.closeSocket(socket));
        if (this.server) {
            this.server.close(() => {
                this.connected = false;
                this.logger.info('Server closed');
            });
        }
    }
