const VERSION_API_URL = 'https://api.example.com/versions';
const SCHEMA_API_URL = 'https://api.example.com/schema/';

async function fetchAndCacheVersions() {
    try {
        if (!fs.existsSync(CACHE_DIR)) {
            fs.mkdirSync(CACHE_DIR);
        }

        const versionResponse = await axios.get(VERSION_API_URL);
        const versions = versionResponse.data;

        for (const version of versions) {
            if (!version.deprecated) {
                try {
                    const schemaResponse = await axios.get(`${SCHEMA_API_URL}${version.id}`);
                    const schemaData = schemaResponse.data;

                    fs.writeFileSync(path.join(CACHE_DIR, `${version.id}.json`), JSON.stringify(schemaData, null, 2));
                    console.log(`Cached schema for version: ${version.id}`);
                } catch (schemaError) {
                    console.error(`Failed to fetch schema for version ${version.id}:`, schemaError.message);
                }
            }
        }
    } catch (error) {
        console.error('Error fetching version data:', error.message);
        process.exit(1);
    }
}

