class ServerDiscovery {
    constructor() {
        this.nearbyServers = {};
        this.lastDiscoveryBroadcast = 0;
        this.broadcastIP = this.calcBroadcastIP();
        this.discoveryListener = null;
    }

    setupDiscovery(callback) {
        const dgram = require('dgram');
        this.discoveryListener = dgram.createSocket('udp4');
        this.discoveryListener.on('message', this.discoveryReceive.bind(this));
        this.discoveryListener.bind(41234, () => {
            if (callback) callback();
        });
    }

    discoveryBroadcast(type, message, callback) {
        const dgram = require('dgram');
        const client = dgram.createSocket('udp4');
        const msg = JSON.stringify({ type, ...message });
        client.send(msg, 0, msg.length, 41234, this.broadcastIP, (err) => {
            client.close();
            if (callback) callback(err);
        });
        this.lastDiscoveryBroadcast = Date.now();
    }

    discoveryReceive(msg, rinfo) {
        const message = JSON.parse(msg.toString());
        if (message.type === 'heartbeat') {
            this.nearbyServers[rinfo.address] = message;
        }
    }

    shutdownDiscovery() {
        if (this.discoveryListener) {
            this.discoveryListener.close();
            this.discoveryListener = null;
        }
    }

    calcBroadcastIP() {
        const os = require('os');
        const interfaces = os.networkInterfaces();
        for (const interface in interfaces) {
            for (const addr of interfaces[interface]) {
                if (addr.family === 'IPv4' && !addr.internal) {
                    const [ip] = addr.address.split('.');
                    return `${ip}.255`;
                }
            }
        }
        return '255.255.255.255';
    }
}

