            this._ws.on('open', () => {
                this.emit('connected');
                resolve();
            });
            this._ws.on('message', (message) => this._handleMessage(message));
            this._ws.on('close', () => this._handleConnectionClose());
            this._ws.on('error', (error) => {
                this.emit('error', error);
                reject(error);
            });
        });
