        this.emit('disconnected');
        this._ws = null;
    }

    _handleMessage(message) {
        const parsedMessage = JSON.parse(message);
        if (parsedMessage.id && this._callbacks[parsedMessage.id]) {
            const callback = this._callbacks[parsedMessage.id];
            delete this._callbacks[parsedMessage.id];
            callback(null, parsedMessage.result || parsedMessage.error);
